#pragma once

#include <deque>
#include <tss/tokenisation/ITokenizer.h>
#include <tss/tokenisation/TokenType.h>
#include <tss/MistakesContainer.h>

class BasicTokenizer final : ITokenizer
{
public:
    explicit BasicTokenizer(const std::string& code, MistakesContainer& mistakes);
    BasicTokenizer(const BasicTokenizer&) = delete;
    BasicTokenizer& operator=(const BasicTokenizer&) = delete;

    BasicTokenizer(BasicTokenizer&&) noexcept = default;
    BasicTokenizer& operator=(BasicTokenizer&&) noexcept = default;

    ~BasicTokenizer() override = default;
    Token GetNextToken() override;
    [[nodiscard]] bool Empty() const override  { return m_tokens.empty(); }
    [[nodiscard]] size_t Size() const override { return m_tokens.size(); }
private:

    TokenType m_lastType { TokenType::LeftParenthesis };
    std::deque<Token> m_tokens;
    std::string_view m_code;
    unsigned int m_cursor { 0 };

    unsigned int m_line { 1 };
    unsigned int m_linePos { 1 };

    Token ParseToken(MistakesContainer& mistakes);
};

#pragma once

#include <deque>
#include <tss/tokenisation/ITokenizer.h>
#include <tss/tokenisation/TokenType.h>
#include <tss/MistakesContainer.h>

class BasicTokenizer final : ITokenizer
{
public:
    explicit BasicTokenizer(const std::string& code, MistakesContainer& mistakes);
    BasicTokenizer(const BasicTokenizer&) = delete;
    BasicTokenizer& operator=(const BasicTokenizer&) = delete;

    BasicTokenizer(BasicTokenizer&&) noexcept = default;
    BasicTokenizer& operator=(BasicTokenizer&&) noexcept = default;

    ~BasicTokenizer() override = default;
    Token GetNextToken() override;
    [[nodiscard]] bool Empty() const override  { return m_tokens.empty(); }
    [[nodiscard]] size_t Size() const override { return m_tokens.size(); }
private:
    TokenType m_lastType { TokenType::LeftParenthesis };
    std::deque<Token> m_tokens;
    std::string m_code_storage; // Owns the string
    std::string_view m_code;    // Views the string
    unsigned int m_cursor { 0 };
    unsigned int m_line { 1 };
    unsigned int m_linePos { 1 };
    Token ParseToken(MistakesContainer& mistakes);
};
